package com.itheima.ssm.dao;import com.itheima.ssm.domain.Product;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Select;import	java.util.List;/** * 完成商品类持久化操作 */public interface IProductDao {    /**     * 查询所有商品信息     * @return     * @throws Exception     */    @Select("SELECT * FROM PRODUCT")    List<Product> findAll() throws Exception;    @Insert("insert into product(productNum,productName,cityName,departureTime,productPrice," +            "productDesc,productStatus) values(#{productNum},#{productName},#{cityName}," +            "#{departureTime},#{productPrice},#{productDesc},#{productStatus})")    void save(Product product) throws Exception;}