package com.itheima.ssm.service.impl;import com.github.pagehelper.PageHelper;import com.itheima.ssm.dao.IOrderDao;import com.itheima.ssm.domain.Orders;import com.itheima.ssm.service.IOrdersService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @auther wangyang * @date 2019/10/23 12:02 AM */@Service@Transactionalpublic class OrdersServiceImpl implements IOrdersService {    @Autowired    private IOrderDao orderDao;    @Override    public List<Orders> findAll(int page, int pageSize) throws Exception{        PageHelper.startPage(page,pageSize);        return orderDao.findAll();    }    @Override    public Orders findById(String id) throws Exception {        return orderDao.findOrderById(id);    }}