package com.itheima.ssm.service.impl;import	java.awt.Desktop.Action;import	java.sql.Wrapper;import com.itheima.ssm.dao.IPermissionDao;import com.itheima.ssm.domain.Permission;import com.itheima.ssm.service.IPermissionService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @auther wangyang * @date 2019/10/24 8:21 PM */@Service@Transactionalpublic class PermissionServiceImpl implements IPermissionService {    @Autowired    private IPermissionDao permissionDao;    @Override    public List<Permission> findAll() {        return permissionDao.findAll();    }    @Override    public void save(Permission permission) {        permissionDao.save(permission);    }}