package com.itheima.ssm.service.impl;import com.itheima.ssm.dao.IProductDao;import com.itheima.ssm.domain.Product;import com.itheima.ssm.service.IProductService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @auther wangyang * @date 2019/10/20 10:23 PM */@Service@Transactionalpublic class ProductImpl implements IProductService {    @Autowired    private IProductDao productDao;    /**     * 获取所有商品信息     * @return     * @throws Exception     */    public List<Product> findAll() throws Exception {        return productDao.findAll();    }    @Override    public void save(Product product) throws Exception{        productDao.save(product);    }}