package com.itheima.ssm.service.impl;import com.itheima.ssm.dao.IRoleDao;import com.itheima.ssm.domain.Permission;import com.itheima.ssm.domain.Role;import com.itheima.ssm.service.IRoleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.List;/** * @auther wangyang * @date 2019/10/24 7:50 PM */@Service@Transactionalpublic class RoleServiceImpl implements IRoleService{    @Autowired    private IRoleDao roleDao;    @Override    public List<Role> findAll() throws Exception{        return roleDao.findAll();    }    @Override    public void save(Role role) throws Exception{        roleDao.save(role);    }    @Override    public List<Role> findOtherRole(String id) throws Exception {        return roleDao.findOtherRole(id);    }    @Override    public Role findById(String id)  throws Exception{        return roleDao.findById(id);    }    @Override    public List<Permission> findOtherPermissions(String id) throws Exception {        return roleDao.findOtherPermissions(id);    }    @Override    public void addPermissionToRole(String roleId, String[] permissionIds) {        for (String permissionId : permissionIds){            roleDao.addPermissionToRole(roleId,permissionId);        }    }}