package com.itheima.ssm.controller;import com.itheima.ssm.domain.Role;import com.itheima.ssm.service.IRoleService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import java.util.List;/** * @auther wangyang * @date 2019/10/24 7:49 PM */@RequestMapping("/role")@Controllerpublic class RoleController {    @Autowired    private IRoleService roleService;    @RequestMapping("/save.do")    public String save(Role role)throws Exception {        roleService.save(role);        return "redirect:findAll.do";    }    @RequestMapping("/findAll.do")    public ModelAndView findAll() throws Exception {        List<Role> roleList = roleService.findAll();        ModelAndView mv = new ModelAndView();        mv.addObject("roleList", roleList);        mv.setViewName("role-list");        return mv;    }}