package com.itheima.ssm.controller;import com.itheima.ssm.domain.SysLog;import com.itheima.ssm.service.ISysLogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.servlet.ModelAndView;import java.util.List;/** * @auther wangyang * @date 2019/10/25 10:25 PM */@Controller@RequestMapping("/sysLog")public class SysLogController {    @Autowired    private ISysLogService sysLogService;    @RequestMapping("/findAll.do")    public ModelAndView findAll() throws Exception{        ModelAndView modelAndView = new ModelAndView();        List<SysLog> sysLogList = sysLogService.findAll();        modelAndView.addObject("sysLogs",sysLogList);        modelAndView.setViewName("syslog-list");        return modelAndView;    }}